mapper.select:
select mentoring.*, me_nickname as ment_me_nickname, mentIf_exp as ment_mentIf_exp, mentIf_ment_job as ment_mentIf_job from mentoring join `member` on me_id = ment_me_id join mentor_info on mentIf_me_id = ment_me_id where ( ment_title like concat('%', #{cri.search}, '%') or ment_content like concat('%', #{cri.search}, '%') or me_nickname like concat('%', #{cri.search}, '%') ) and (
mentIf_ment_job like concat('%', #{job.ment_job}, '%')
)
order by ment_num desc limit #{cri.pageStart}, #{cri.perPageNum}

select count(*) from mentoring join `member` on me_id = ment_me_id join mentor_info on mentIf_me_id = ment_me_id where ( ment_title like concat('%', #{cri.search}, '%') or ment_content like concat('%', #{cri.search}, '%') or me_nickname like concat('%', #{cri.search}, '%') ) and (
mentIf_ment_job like concat('%', #{job.ment_job}, '%')
)
