mapper.select:
SELECT *
FROM recommend
WHERE reco_table = #{recommend.reco_table} and reco_target = #{recommend.reco_target} and reco_me_id = #{recommend.reco_me_id}

mapper.update:
UPDATE recommend
	SET reco_state = #{recommend.reco_state}
WHERE reco_table = #{recommend.reco_table} and reco_target = #{recommend.reco_target} and reco_me_id = #{recommend.reco_me_id}
